package com.javaex.practice;

public class Ex02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 0;
		//x가 0과 같으면
		/*
		if(x=0) {
			System.out.println("x는 0이다.");
		}
		*/
		if(x==0) {
			System.out.println("x는 0이다.");
		}
		//x는 0과 같다를 표현하고 싶으면 x=0이 아니라 x==0을 사용해야함
		//x=0은 x에 0을 넣는다는 말이기 때문에 조건문에는 조건이 와야 하기 때문에 잘 못됨.
	}

}
